import React, {Component} from 'react';
import { Link } from 'react-router-dom';

class varmilo extends Component {

    constructor(props) {
        super(props);
        this.state = {
            item: [],
            isLoaded: false,
        }
    }

    componentDidMount() {
        fetch('http://localhost:3030/varmilo')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
            })
        });
    }

    render() {
        
        var { isLoaded, items } = this.state;

        if(!isLoaded) {
            return <div>Loading...</div>;
        }
        else {
            return(
                <div>
                    <div className="container brand-tag border border-bottom-0 brand-div">
                        <img src="assets/img/varmilo-logo-black.png" alt="" className="mt-2 logo2" width="25%"/>
                    </div>
                    <div className="container home-container">
                        <div className="row text-center border">
                            {items.map(item => (           
                                <div className="col-3 border p-1" key={item.id}>
                                    <Link to={`/shop/${item.id}`}><img src={item.img} alt="" width="100%" /></Link>
                                    <p className="pt-2 item-des">{item.name}</p>
                                    <p className="mb-2"><strong>${item.price}</strong></p>              
                                </div>
                            ))}
                        </div>
                    </div>
                </div>      
            );
        }
    }
}

export default varmilo;
